INSERT INTO DATA.MOVIE(ID, FILE_NAME, NAME, GENRE_ID, SERIE_ID, LANGUAGE, SUBTITLES, ORIGINAL_LANGUAGE, ORIGINAL_NAME, URL, PICTURE_FILE_NAME, DISPLAY_ORDER, DESCRIPTION, CREATED) VALUES
(2032, '201303/Mickey - Traja musketieri cz.m4v', 'Mickey - Traja musketieri', 3, 25, 'cz', NULL, 'en', 'Mickey Mouse Three Musketeers', 'http://www.csfd.cz/film/190186-tri-musketyri/', 'Mickey - Traja musketieri cz.jpg', 303, STRINGDECODE('Nejlep\u0161\u00ed p\u0159\u00e1tel\u00e9 Mickey, Donald a Goofy jsou pouze bezv\u00fdznamn\u00fdmi pomocn\u00edky, ale sn\u00ed o kari\u00e9\u0159e prav\u00fdch mu\u0161ket\u00fdr\u016f. \u017divot se jim \u00fapln\u011b zm\u011bn\u00ed ve chv\u00edli, kdy jednonoh\u00fd kapit\u00e1n mu\u0161ket\u00fdr\u016f Pete p\u0159ipravuje stra\u0161n\u00e9 spiknut\u00ed. Chce odstranit princeznu Minnii, jedinou my\u0161 v kr\u00e1lovstv\u00ed, kter\u00e1 mu stoj\u00ed v cest\u011b k tr\u016fnu. A aby m\u011bl uleh\u010denou pr\u00e1ci, ud\u011bl\u00e1 z na\u0161ich p\u0159\u00e1tel princezniny str\u00e1\u017ee. Jen\u017ee Mickey, Donald a Goofy nejsou zrovna p\u0159\u00edkladem neohro\u017een\u00fdch hrdin\u016f, ale spoj\u00ed sv\u00e9 s\u00edly a p\u0159iprav\u00ed Petovi nemil\u00e9 p\u0159ekvapen\u00ed. Jinak to ani nem\u016f\u017ee dopadnout, kdy\u017e jeden bojuje za v\u0161echny a v\u0161ichni z jednoho!(ofici\u00e1ln\u00ed text distributora)'), NULL),
(779, '5th Element.m4v', '5th Element', 4, NULL, 'cz', '  ', 'en', NULL, 'http://www.imdb.com/title/tt0119116/', '5th Element.jpg', 5, 'Directed by Luc Besson. With Bruce Willis, Milla Jovovich, Gary Oldman, Ian Holm. In the colorful future, a cab driver unwittingly becomes the central figure in the search for a legendary cosmic weapon to keep Evil and Mr Zorg at bay.', NULL),
(1467, 'The Godfather-en.m4v', 'The Godfather', 15, 1003, 'en', 'cz', 'en', 'The Godfather', 'http://www.csfd.cz/film/1644-kmotr/', 'The Godfather-en.jpg', 1, STRINGDECODE('Gangstersk\u00e9 drama Kmotr, nato\u010den\u00e9 podle stejnojmenn\u00e9ho bestselleru Maria Puza, pat\u0159\u00ed mezi p\u0159elomov\u00e1 d\u00edla tzv. Nov\u00e9ho Hollywoodu, a to jak komer\u010dn\u011b, tak um\u011blecky. P\u0159\u00edb\u011bh newyorsk\u00e9 mafi\u00e1nsk\u00e9 rodiny Corleon\u016f, j\u00ed\u017e tvrdou rukou \u0161\u00e9fuje star\u00fd don Vito (Marlon Brando), se odehr\u00e1v\u00e1 v letech 1945 - 55 a zaznamen\u00e1v\u00e1 prudk\u00fd rozmach organizovan\u00e9ho zlo\u010dinu po skon\u010den\u00ed druh\u00e9 sv\u011btov\u00e9 v\u00e1lky. V epick\u00fdch, rozm\u00e1chl\u00fdch obrazech p\u0159ed div\u00e1kem o\u017e\u00edvaj\u00ed osudy italsko-americk\u00e9 rodiny, je\u017e sv\u016fj blahobyt zalo\u017eila na hazardu, prostituci, vyd\u00edr\u00e1n\u00ed a zastra\u0161ov\u00e1n\u00ed. Tehdy mlad\u00fd re\u017eis\u00e9r Francis Ford Coppola, kter\u00fd byl pon\u011bkud p\u0159ekvapiv\u011b pov\u011b\u0159en spole\u010dnost\u00ed Paramount realizac\u00ed jednoho z jej\u00edch st\u011b\u017eejn\u00edch projekt\u016f (sv\u00e9 nepochybn\u011b sehr\u00e1lo i jeho italsko-americk\u00e9 z\u00e1zem\u00ed), musel redukovat n\u011bkter\u00e9 motivy rozs\u00e1hl\u00e9 p\u0159edlohy, ov\u0161em poda\u0159ilo se mu v\u011brn\u011b zachovat jej\u00edho ducha - p\u0159esn\u00fd popis fungov\u00e1n\u00ed zlo\u010dineck\u00e9 organizace, zachycen\u00ed jej\u00ed hierarchie, za\u010dlen\u011bn\u00ed do tehdej\u0161\u00edho spole\u010densk\u00e9ho v\u00fdvoje i \u010dasto kritizovan\u00e9 zd\u016fraz\u0148ov\u00e1n\u00ed rodinn\u00e9 soudr\u017enosti. Pevnou dramatickou stavbu sc\u00e9n\u00e1\u0159e (na n\u011bm\u017e Coppola spolupracoval s autorem p\u0159edlohy) podpo\u0159ilo i re\u017eijn\u00ed ztv\u00e1rn\u011bn\u00ed, je\u017e \u010derpalo p\u0159edev\u0161\u00edm z klasick\u00fdch vyprav\u011b\u010dsk\u00fdch praktik hollywoodsk\u00fdch d\u011bl 30. let (nap\u0159. Jih proti Severu), oko\u0159en\u011bn\u00fdch soudob\u00fdm p\u0159\u00edklonem k realismu, zejm\u00e9na v zobrazov\u00e1n\u00ed n\u00e1sil\u00ed a sexu. Sn\u00edmek z\u00edskal t\u0159i Oscary - za nejlep\u0161\u00ed film, sc\u00e9n\u00e1\u0159 podle p\u0159edlohy a mu\u017esk\u00fd hereck\u00fd v\u00fdkon v hlavn\u00ed roli (Marlon Brando, kter\u00fd svou cenu odm\u00edtl p\u0159evz\u00edt). Pro \u00fasp\u011bch filmu, kter\u00fd se stal div\u00e1ck\u00fdm hitem nejen ve Spojen\u00fdch st\u00e1tech, ale i ve zbytku sv\u011bta (krom\u011b nap\u0159. tehdej\u0161\u00edho \u010ceskoslovenska, kde se kv\u016fli \u00fadajn\u00e9 glorifikaci zlo\u010dinu a n\u00e1sil\u00ed nesm\u011bl prom\u00edtat, a\u010d Puzova kniha v \u010desk\u00e9m p\u0159ekladu vy\u0161la), bylo kl\u00ed\u010dov\u00e9 hereck\u00e9 obsazen\u00ed hlavn\u00edch \u00faloh dona Vita Corleona a jeho nejmlad\u0161\u00edho syna Michaela. Veden\u00ed Paramountu nesouhlasilo s Marlonem Brandem, na n\u011bm\u017e mu vedle jeho dob\u0159e zn\u00e1m\u00e9 pov\u011bsti \"obt\u00ed\u017en\u00e9ho a nespolehliv\u00e9ho herce, jeho\u017e posledn\u00ed filmy byly propad\u00e1ky\", vadilo hlavn\u011b to, \u017ee je na roli mafi\u00e1nsk\u00e9ho patriarchy p\u0159\u00edli\u0161 mlad\u00fd (v dob\u011b p\u0159\u00edprav filmu mu bylo 47 let), a je\u0161t\u011b v\u011bt\u0161\u00ed odpor d\u00e1valo najevo v\u016f\u010di Al Pacinovi, jen\u017e se jim zd\u00e1l m\u00e1lo zn\u00e1m\u00fd, p\u0159\u00edli\u0161 oby\u010dejn\u00fd a p\u0159edev\u0161\u00edm mal\u00fd. Coppola s ob\u011bma herci nato\u010dil testy, je\u017e nakonec \u0161\u00e9fy Paramountu p\u0159esv\u011bd\u010dily. Se stejnou pe\u010dlivost\u00ed re\u017eis\u00e9r obsadil i dal\u0161\u00ed role, nejstar\u0161\u00edho Corleonova syna, prchliv\u00e9ho Sonnyho (James Caan), jeho slabo\u0161sk\u00e9ho bratra Freda (John Cazale) a conseglieriho Toma Hagena (Robert Duvall). \u00dalohy Corleonov\u00fdch \"voj\u00e1k\u016f\" a dal\u0161\u00edch gangster\u016f zt\u011blesnili vesm\u011bs m\u00e1lo zn\u00e1m\u00ed p\u0159edstavitel\u00e9, kte\u0159\u00ed dokonale typov\u011b odpov\u00eddali sv\u00fdm postav\u00e1m (nap\u0159. Richard Castellano jako Clemenza nebo Al Lettieri jako Sollozzo). V kreaci Michaelovy neitalsk\u00e9 d\u00edvky Kay Adamsov\u00e9 zaujala budouc\u00ed m\u00faza Woodyho Allena Diane Keatonov\u00e1 a Corleonovu t\u00fdranou dceru Connii zt\u011blesnila pozd\u011bj\u0161\u00ed Stalloneova partnerka z Rockyho, Coppolova mlad\u0161\u00ed sestra Talia Shireov\u00e1. T\u00e9m\u011b\u0159 t\u0159\u00edhodinov\u00fd sn\u00edmek (kter\u00fd uv\u00e1d\u00edme, stejn\u011b jako dal\u0161\u00ed d\u00edly trilogie, ve dvou \u010d\u00e1stech) vrchol\u00ed slavnou sc\u00e9nou k\u0159tu Michaelova synovce (p\u0159edstavovan\u00e9ho Coppolovou dcerou Sofi\u00ed), prost\u0159\u00edhan\u00e9ho krvav\u00fdm vy\u0159izov\u00e1n\u00edm \u00fa\u010dt\u016f s ostatn\u00edmi mafi\u00e1nsk\u00fdmi rodinami, je\u017e znamenalo upevn\u011bn\u00ed moci Corleonova klanu, ale tak\u00e9 definitivn\u00ed p\u0159evzet\u00ed \"rodinn\u00e9 firmy\" zprvu zdr\u00e1hav\u00fdm Michaelem, kter\u00fd se st\u00e1v\u00e1 d\u016fstojn\u00fdm n\u00e1stupcem zem\u0159el\u00e9ho otce.(ofici\u00e1ln\u00ed text distributora)'), NULL);
