(816, 'Limonadovy Joe.m4v', 'Limonadovy Joe', 9, NULL, 'cz', '  ', 'cz', NULL, 'http://www.csfd.cz/film/5991-limonadovy-joe-aneb-konska-opera/', 'Limonadovy Joe.jpg', 60, STRINGDECODE('<strong>Jedna z nejslavn\u011bj\u0161\u00edch a nejobl\u00edben\u011bj\u0161\u00edch \u010desk\u00fdch filmov\u00fdch komedi\u00ed, kde spr\u00e1vn\u00ed mu\u017ei pij\u00ed pouze kolalokovu lihuprostou limon\u00e1du a z\u00e1kon m\u00e1 podobu pistoln\u00edka, vybaven\u00e9ho p\u0159esnou mu\u0161kou, z\u00e1\u0159iv\u00fdm \u00fasm\u011bvem a lahodn\u00fdm tenorem.</strong><br />'), NULL),
(818, 'Malery pana uctovnika 1 - Fantozzi.m4v', 'Malery pana uctovnika 1 - Fantozzi', 9, NULL, 'cz', '  ', 'it', 'Fantozzi contro tutti', 'http://www.csfd.cz/film/32729-malery-pana-ucetniho/', 'Malery pana uctovnika 1 - Fantozzi.jpg', 100, STRINGDECODE('Bl\u00e1zniv\u00e1 italsk\u00e1 komedie o chlap\u00edkovi, kter\u00fd m\u011bl doma \u0161karedou \u017eenu, v pr\u00e1ci p\u0159\u00edsn\u00e9ho \u0161\u00e9fa, a v\u0161echno to sn\u00e1\u0161el v\u00edc ne\u017e state\u010dn\u011b.<br /><br />'), NULL);
