INSERT INTO data.movie VALUES (2032, '201303/Mickey - Traja musketieri cz.m4v', 'Mickey - Traja musketieri', 3, 25, 'cz', NULL, 'en', 'Mickey Mouse Three Musketeers', 'http://www.csfd.cz/film/190186-tri-musketyri/', 'Mickey - Traja musketieri cz.jpg', 303, 'Nejlepší přátelé Mickey, Donald a Goofy jsou pouze bezvýznamnými pomocníky, ale sní o kariéře pravých mušketýrů. Život se jim úplně změní ve chvíli, kdy jednonohý kapitán mušketýrů Pete připravuje strašné spiknutí. Chce odstranit princeznu Minnii, jedinou myš v království, která mu stojí v cestě k trůnu. A aby měl ulehčenou práci, udělá z našich přátel princezniny stráže. Jenže Mickey, Donald a Goofy nejsou zrovna příkladem neohrožených hrdinů, ale spojí své síly a připraví Petovi nemilé překvapení. Jinak to ani nemůže dopadnout, když jeden bojuje za všechny a všichni z jednoho!(oficiální text distributora)', NULL);
