INSERT INTO data.movie VALUES (860, 'Valmont.m4v', 'Valmont', 6, NULL, 'cz', '  ', 'fr', 'Valmont', 'http://www.csfd.cz/film/2986-valmont/', 'Valmont.jpg', 70, 'Filmová adaptace slavného románu Choderlose de Laclose Nebezpečné známosti (1782), která na spletitém osudu dvou libertinských intrikánů, vikomta Valmonta a markýzy de Merteuil, zachycuje v poněkud odlehčené poloze obraz aristokratické společnosti několik let před Francouzskou revolucí. Příběh cynických poživačníků je podán v sytých barvách pozdního baroka s důrazem na malebné plenéry, vtipné epizody a bohatou výpravu a kostýmy.(oficiální text distributora)', NULL);
