INSERT INTO data.movie VALUES (20000, '201406/ANGEL_HEART.m4v', 'Angel Heart', 2, 0, 'cz', NULL, 'en', 'Angel Heart', 'http://www.csfd.cz/film/7109-angel-heart/', 'ANGEL_HEART.jpg', NULL, 'Amerika roku 1955, jazz a místa v autobusech vyhrazená jen pro bílé. Soukromé očko Harry Angel (Mickey Rourke) dostane neobvyklý kšeft - pro podivínského právníka jménem Louis Cyphre vypátrat jistého Johnyho Favoritea, kterého 12 let nikdo neviděl. Angel se vydává za stopou a místo odpovědí nachází jen další otázky. Poté, co kolem něj začnou umírat svědkové, Angel pochopí, že tohle není jen tak lecjaká práce a že mu začíná jít o krk...(ČSFD.cz)', '2014-06-23 20:56:32.238');
