INSERT INTO data.movie VALUES (936, 'Detske/Karlik a tovaren na cokoladu -- Charlie and The Chocolate Factory.m4v', 'Karlik a tovaren na cokoladu', 3, NULL, 'cz', '  ', 'en', 'Charlie and The Chocolate Factory', 'http://www.csfd.cz/film/135983-karlik-a-tovarna-na-cokoladu/', 'Karlik a tovaren na cokoladu -- Charlie and The Chocolate Factory.jpg', 80, 'Charlie Bucket je malý chlapec, pochádzajúci z celkom zbedačenej rodiny, býva na okraji mesta hneď vedľa obrovskej továrne na čokoládu. Charlie miluje čokoládu, aj keď dostáva čokoládový bombón len raz do roka. Jedného dňa však vyhrá aj s ďalšími štyrmi deťmi cestu po fantastickej čokoládovej továrni, ktorú vlastní excentrický Willy Wonka a jeho nemenej zaujímaví zamestnanci.(oficiální text distributora)', NULL);
INSERT INTO data.movie VALUES (937, 'Detske/Kniha dzungli.m4v', 'Kniha dzungli', 3, NULL, 'cz', '  ', 'en', NULL, 'http://www.csfd.cz/film/277253-kniha-dzungli-maugliho-dobrodruzstvi/', 'Kniha dzungli.jpg', 27, 'Světoznámý román Rudyarda Kiplinga je příběhem malého chlapce Mauglího, který se ztratil v džungli a byl adoptován rodinou vlků. Neustále pronásledován zlým bengálským tygrem Šér Chánem, prožívá Mauglí svá dobrodružství společně se svojí vlčí rodinou pod ochranou černého pantera Baghíry a moudrého medvěda Balúa, který učí zákonům džungle nejen chlapce, ale i celou jeho vlčí smečku.(oficiální text distributora)', NULL);
INSERT INTO data.movie VALUES (938, 'Detske/Lisiacik Vuk.m4v', 'Lisiacik Vuk', 3, NULL, 'sk', '  ', 'en', NULL, 'http://www.csfd.cz/film/94781-lisacek-lup/', 'Lisiacik Vuk.jpg', 26, 'Malý lišáček Vuk je velký nezbedník. Není však sám, v problémech mu pomáhá věrný kamarád Karak. Dohromady musí čelit velkému nebezpečí v podobě zlého lovce. Podaří se jim to? Musí zachránit přítelkyni lišku a zažijí při tom i mnoho veselých dobrodružství a příhod.(oficiální text distributora)', NULL);
