INSERT INTO data.movie VALUES (1082, 'James Bond/1965 James Bond - Thunderball.m4v', 'Thunderball', 15, 15, 'cz', '  ', 'en', 'Thunderball', 'http://www.imdb.com/title/tt0059800/', '1965 James Bond - Thunderball.jpg', 4, 'Directed by Terence Young. With Sean Connery, Claudine Auger, Adolfo Celi, Luciana Paluzzi. James Bond heads to The Bahamas to recover two nuclear warheads stolen by SPECTRE agent Emilio Largo in an international extortion scheme.', NULL);
INSERT INTO data.movie VALUES (1083, 'James Bond/1967 James Bond - Zijes len dvakrat -- You only live twice.m4v', 'Zijes len dvakrat', 15, 15, 'cz', '  ', 'en', 'You only live twice', 'http://www.imdb.com/title/tt0062512/', '1967 James Bond - Zijes len dvakrat -- You only live twice.jpg', 5, 'Directed by Lewis Gilbert. With Sean Connery, Akiko Wakabayashi, Mie Hama, Tetsur&#xF4; Tanba. Agent 007 and the Japanese secret service ninja force must find and stop the true culprit of a series of spacejackings before nuclear war is provoked.', NULL);
INSERT INTO data.movie VALUES (1085, 'James Bond/1971 James Bond - Diamanty su vecne -- Diamonds Are Forever.m4v', 'Diamanty su vecne', 15, 15, 'cz', '  ', 'en', 'Diamonds Are Forever', 'http://www.imdb.com/title/tt0066995/', '1971 James Bond - Diamanty su vecne -- Diamonds Are Forever.jpg', 7, 'Directed by Guy Hamilton. With Sean Connery, Jill St. John, Charles Gray, Lana Wood. A diamond smuggling investigation leads James Bond to Las Vegas, where he uncovers an extortion plot headed by his nemesis, Ernst Stavro Blofeld.', NULL);
INSERT INTO data.movie VALUES (1086, 'James Bond/1973 James Bond - Zit a nechat zomriet -- Live and Let Die.m4v', 'Zit a nechat zomriet', 15, 15, 'cz', '  ', 'en', 'Live and Let Die', 'http://www.imdb.com/title/tt0070328/', '1973 James Bond - Zit a nechat zomriet -- Live and Let Die.jpg', 8, 'Directed by Guy Hamilton. With Roger Moore, Yaphet Kotto, Jane Seymour, Clifton James. 007 is sent to stop a diabolically brilliant heroin magnate armed with a complex organization and a reliable psychic tarot card reader.', NULL);
INSERT INTO data.movie VALUES (1087, 'James Bond/1974 James Bond - Muz so zlatou zbranou -- The Man with the Golden Gun.m4v', 'Muz so zlatou zbranou', 15, 15, 'cz', '  ', 'en', 'The Man with the Golden Gun', 'http://www.imdb.com/title/tt0071807/', '1974 James Bond - Muz so zlatou zbranou -- The Man with the Golden Gun.jpg', 9, 'Directed by Guy Hamilton. With Roger Moore, Christopher Lee, Britt Ekland, Maud Adams. Bond is led to believe that he is targeted by the world&#x27;s most expensive assassin and must hunt him down to stop him.', NULL);
INSERT INTO data.movie VALUES (1088, 'James Bond/1977 James Bond - Agent, ktory ma miloval -- The Spy Who Loved Me.m4v', 'Agent, ktory ma miloval', 15, 15, 'cz', '  ', 'en', 'The Spy Who Loved Me', 'http://www.imdb.com/title/tt0076752/', '1977 James Bond - Agent, ktory ma miloval -- The Spy Who Loved Me.jpg', 10, 'Directed by Lewis Gilbert. With Roger Moore, Barbara Bach, Curd J&#xFC;rgens, Richard Kiel. James Bond investigates the hijacking of British and Russian submarines carrying nuclear warheads with the help of a KGB agent whose lover he killed.', NULL);
INSERT INTO data.movie VALUES (1089, 'James Bond/1979 James Bond - Moonraker.m4v', 'Moonraker', 15, 15, 'cz', '  ', 'en', 'Moonraker', 'http://www.imdb.com/title/tt0079574/', '1979 James Bond - Moonraker.jpg', 11, 'Directed by Lewis Gilbert. With Roger Moore, Lois Chiles, Michael Lonsdale, Richard Kiel. James Bond investigates the mid-air theft of a space shuttle and discovers a plot to commit global genocide.', NULL);
INSERT INTO data.movie VALUES (1090, 'James Bond/1981 James Bond - Len pre tvoje oci -- For Your Eyes Only.m4v', 'Len pre tvoje oci', 15, 15, 'cz', '  ', 'en', 'For Your Eyes Only', 'http://www.imdb.com/title/tt0082398/', '1981 James Bond - Len pre tvoje oci -- For Your Eyes Only.jpg', 12, 'Directed by John Glen. With Roger Moore, Carole Bouquet, Topol, Lynn-Holly Johnson. Agent 007 is assigned to hunt for a lost British encryption device and prevent it from falling into enemy hands.', NULL);
