INSERT INTO data.movie VALUES (9951, '201405/47_RONIN.m4v', '47 Roninov', 4, 0, 'cz', 'cz', 'en', '47 Ronin', 'http://www.csfd.cz/film/287719-47-roninu/', '47_RONIN.jpg', NULL, 'Rónin je samuraj, který ztratil svého pána a tím i svou čest, a jeho postavení na společenském žebříčku středověkého Japonska tomu odpovídá. Ještě mnohem níž však stojí lidé smíšené rasy, mezi něž patří i nalezenec Kai (Keanu Reeves), zčásti Brit, zčásti Japonec. Díky svým mimořádným schopnostem je vzat na milost skupinou bývalých samurajů, jejichž pán se stal obětí spiknutí, které připravili zákeřný Lord Kira a čarodějka Mizuki. Róninové chtějí Lorda Kiru porazit za každou cenu, jenže trumfy rozhodně nejsou na jejich straně. Dokud nezískají Kaie. I tak ale bude cesta za spravedlivou odplatou trnitá a jen málokdo z nich po ní dojde až do konce, který navíc může mít v souladu s japonskými tradicemi velmi trpkou příchuť.(CinemArt)', '2014-05-29 08:56:04.792');
