INSERT INTO data.movie VALUES (21027, '201407/FRANKENWEENIE.m4v', 'Frankenweenie', 3, 0, 'en', 'cz', 'en', 'Frankenweenie', 'http://www.csfd.cz/film/236117-frankenweenie-domaci-mazlicek/', 'FRANKENWEENIE.jpg', NULL, 'Frankenweenie: Domácí mazlíček je dojemným příběhem chlapce a jeho psa. Když mladý Victor nečekaně přijde o svého milovaného psa Sparkyho, povolá na pomoc vědu a svého nejlepšího přítele přivede znovu k životu – jen s několika drobnými změnami. Snaží se svůj výtvor ukrývat, ale když se Sparkymu podaří uniknout, zjistí Victorovi spolužáci, učitelé i ostatní obyvatelé města, že znovuzrození může mít příšerné následky.(oficiální text distributora)', '2014-07-24 03:10:12.308');
