INSERT INTO data.movie VALUES (1479, 'Pirati/Pirati z Karibiku - Na konci sveta en.m4v', 'At World''s End', 15, 26, 'en', 'cz', 'en', '', 'http://www.imdb.com/title/tt0449088/', 'Pirati z Karibiku - Na konci sveta en.jpg', 3, 'Directed by Gore Verbinski.  With Johnny Depp, Orlando Bloom, Keira Knightley, Geoffrey Rush. Captain Barbossa, Will Turner and Elizabeth Swann must sail off the edge of the map, navigate treachery and betrayal, and make their final alliances for one last decisive battle.', NULL);
INSERT INTO data.movie VALUES (1480, 'Pirati/Pirati z Karibiku - Na vlnach podivna cz.m4v', 'Na vlnach podivna', 15, 26, 'cz', NULL, 'en', 'Pirates of the Caribbean: On Stranger Tides', 'http://www.csfd.cz/film/252716-pirati-z-karibiku-na-vlnach-podivna/', 'Pirati z Karibiku - Na vlnach podivna cz.jpg', 8, 'Johnny Depp se vrací ke své ikonické roli kapitána Jacka Sparrowa v akčním příběhu plném pravdy, zrady, mládí a odchodů. Když se Jack setká se ženou ze své minulosti (Penelope Cruz), není si jistý, zda jde o lásku - nebo zda tato žena jen „hraje na city“ a využívá ho, aby našla legendární Pramen mládí. Když ho donutí, aby vstoupil na palubu Queen Anne’s Revenge, lodi obávaného piráta Černovouse (Ian McShane), Jack se ocitá v nečekaném dobrodružství, kde neví, koho se obávat více: Černovouse nebo ženy ze své minulosti. Film bude uveden v českých kinech ve 3D s dabingem, ve 2D s dabingem i s titulky.(oficiální text distributora)', NULL);
