INSERT INTO data.movie VALUES (35220, '201505/LAPUTA_NEBESKY_ZAMEK.m4v', 'Laputa Nebesky Zamek', 3, 0, 'cz', '  ', 'jp', 'Tenkū no shiro Laputa', 'http://www.csfd.cz/film/28495-laputa-nebesky-zamek/', 'LAPUTA_NEBESKY_ZAMEK.jpg', 0, 'Do náruče Pazua, kluka z hornického městečka, se jednoho dne snese z nebe dívka Sheeta, která má kolem krku tajemný modrý kámen. Brzy zjišťují, že se jedná o mocný kámen z levitačního krystalu, po kterém usilovně pátrají nejen ziskuchtiví vzdušní piráti, ale i tajemní vládní agenti. Krystal totiž neznamená jen velké bohatství, ale může být i klíčem k Laputě – dávnému a legendárnímu městu, které pluje po obloze a ukrývá v sobě nejen obrovské bohatství, ale také obrovskou moc', '2015-05-16 03:10:02.731');
