INSERT INTO data.movie VALUES (2382, '201312-2/LEON.m4v', 'Leon', 15, 0, 'cz', NULL, 'fr', 'Léon', 'http://www.csfd.cz/film/773-leon/', 'LEON.jpg', NULL, 'Strhující thriller o dvanáctileté dívce, která osudově zasáhne do života stárnoucího profesionálního zabijáka. Neobyčejný milostný příběh profesionálního vraha a mladé dívky. Leon je nájemný vrah, člověk, kterého nedokáže nic zastavit před splněním úlohy. Do jeho života vstoupí 12-letá Mathilda. Rodina nevinné Mathildy byla vyvražděná zkorumpovaným policajtem a ona se rozhodla pro krutou pomstu.(oficiální text distributora)', NULL);
INSERT INTO data.movie VALUES (35240, '201505/TOP_GUN.m4v', 'Top Gun', 15, 0, 'cz', '  ', 'en', 'Top Gun', 'http://www.csfd.cz/film/8288-top-gun/', 'TOP_GUN.jpg', 0, 'Mladí vojenští piloti Pete "Maverick" Mitchell a jeho kolega Goose jsou vysláni na pětitýdenní kurz do kalifornského Miramaru do školy pro elitní vojenské piloty, které se říká Top Gun. Maverick je na to hrdý a hodlá se stát nejlepším bojovým pilotem ve vojenském námořnictvu. Avšak jeho hazardérství není obdivováno, naopak mu působí mezi kolegy a nadřízenými potíže, protože Maverick porušuje pravidla a oficiální předpisy. Jeho instinkt je pro něho důležitější. Když však jeho kamarád Goose přijde při jednom zkušebním letu o život, začíná se Maverick měnit...', '2015-05-16 03:10:02.8');
