INSERT INTO data.movie VALUES (2028, '201303/Chronicles of Riddick en.m4v', 'Chronicles of Riddick', 4, 0, 'en', 'en', 'en', 'Chronicles of Riddick', 'http://www.imdb.com/title/tt0296572/?ref_=fn_al_tt_2', 'Chronicles of Riddick en.jpg', NULL, 'Directed by David Twohy.  With Vin Diesel, Judi Dench, Colm Feore, Thandie Newton. 5 years after Pitch Black, the wanted criminal Riddick arrives on a planet called Helion Prime, and finds himself up against an invading empire called the Necromongers, an army that plans to convert or kill all humans in the universe.', NULL);
INSERT INTO data.movie VALUES (2035, '201303/Tom a Jerry -- Rychlo a chlpato cz.m4v', 'Tom a Jerry - Rychlo a chlpato', 3, 30020, 'cz', NULL, 'en', 'Tom a Jerry Fast And Furry', 'http://www.csfd.cz/film/235731-tom-a-jerry-rychle-a-chlupate/', 'Tom a Jerry -- Rychlo a chlpato cz.jpg', NULL, 'Honička začíná! Vaši animovaní oblíbenci byli nemilosrdně vykázáni ze svého domova, protože jej neustále ničili! Jejich první štací se stává televizní reality show, kam se přihlásí jako soutěžící - protože hlavní cenou je obrovský, překrásný dům. Tom s Jerrym se okamžitě vyhoupnou na špičku žebříčku sledovanosti. A není divu, od superrychlých vozů přes podvodní stíhačky až po finálový souboj letadel s raketovým pohonem se každý další závod stává větší výzvou než byl ten předchozí. Nenechte si ujít rodinnou zábavu v délce celovečerního filmu! Jako diváci nemůžete prohrát.(Magic Box)', NULL);
