INSERT INTO data.movie VALUES (2388, '201312-2/THE_WOLVERINE.m4v', 'Wolverine', 4, 28, 'en', 'cz', NULL, 'The Wolverine', 'http://www.imdb.com/title/tt1430132/?ref_=nv_sr_1', 'THE_WOLVERINE.jpg', 6, 'Directed by James Mangold.  With Hugh Jackman, Will Yun Lee, Tao Okamoto, Rila Fukushima. When Wolverine is summoned to Japan by an old acquaintance, he is embroiled in a conflict that forces him to confront his own demons.', NULL);
INSERT INTO data.movie VALUES (2252, '201309/UZASNAKOVI SK.m4v', 'Rodinka uzasnych', 3, 0, 'sk', NULL, 'en', 'The Incredibles', 'http://www.csfd.cz/film/127213-uzasnakovi/', 'UZASNAKOVI SK.jpg', NULL, 'Bob Parr, známy ako Mr. Úžasný, býval jedným z najväčších svetových superhrdinov a zachraňovanie sveta bolo jeho denným chlebom. Svoj superoblek musel zavesiť na klinec pred pätnástimi rokmi. Teraz pracuje ako odhadca škôd pre poisťovňu a bojuje s nudou a rastúcim bruchom. So svojou manželkou (bývalou Elastiženou) a tromi deťmi sa pokúšajú žiť normálny život. Jeho chvíľa prichádza, keď ho tajomný kontakt vyšle na vzdialený ostrov s tajným poslaním. Tam zistí, že génius zla pripravuje voči svetu diabolskú pomstu.(oficiální text distributora)', NULL);
