INSERT INTO data.movie VALUES (2023, '201301/Mechanicky pomeranc -- Clockwork Orange.m4v', 'Mechanicky pomaranc', 15, 0, 'en', 'cz', 'en', 'Clockwork Orange', 'http://www.imdb.com/title/tt0066921/?ref_=sr_1', 'Mechanicky pomeranc -- Clockwork Orange.jpg', NULL, 'Directed by Stanley Kubrick.  With Malcolm McDowell, Patrick Magee, Michael Bates, Warren Clarke. In future Britain, charismatic delinquent Alex DeLarge is jailed and volunteers for an experimental aversion therapy developed by the government in an effort to solve society''s crime problem - but not all goes according to plan.', NULL);
