INSERT INTO data.movie VALUES (2378, '201312-2/APOCALYPSE_NOW.m4v', 'Apocalypse Now', 15, 0, 'en', NULL, 'cz', NULL, 'http://www.imdb.com/title/tt0078788/?ref_=nv_sr_1', 'APOCALYPSE_NOW.jpg', NULL, 'Directed by Francis Ford Coppola.  With Martin Sheen, Marlon Brando, Robert Duvall, Frederic Forrest. During the U.S.-Viet Nam War, Captain Willard is sent on a dangerous mission into Cambodia to assassinate a renegade colonel who has set himself up as a god among a local tribe.', NULL);
