CREATE SCHEMA data;

CREATE TABLE data.batch_log (
    id bigint auto_increment,
    created timestamp,
    "text" text
);

CREATE TABLE data.genre (
    id bigint auto_increment,
    name character varying(128) NOT NULL,
    picture_file_name character varying(512),
    display_order bigint,
    protected bigint DEFAULT 0 NOT NULL,
    created timestamp
);

CREATE TABLE data.language (
    id character varying(2) NOT NULL,
    name character varying(128) NOT NULL,
    picture_file_name character varying(512),
    display_order bigint,
    created timestamp
);

CREATE TABLE data.movie (
    id bigint auto_increment,
    file_name character varying(512),
    name character varying(128),
    genre_id bigint,
    serie_id bigint,
    language character varying(2),
    subtitles character varying(2),
    original_language character varying(2),
    original_name character varying(128),
    url character varying(128),
    picture_file_name character varying(512),
    display_order bigint,
    description text,
    created timestamp
);

CREATE TABLE data.serie (
    id bigint auto_increment,
    name character varying(128),
    picture_file_name character varying(512),
    display_order bigint,
    genre_id bigint,
    created timestamp,
    tv_show boolean
);





